param()

$ErrorActionPreference = "Stop"
$here = Split-Path -Parent $MyInvocation.MyCommand.Path
$root = Split-Path -Parent $here

function Abort($reason){
  Write-Host ("ABORTED gate=G10_DESIGN_REVIEW_PACK reason={0}" -f $reason)
  exit 1
}

# Validate repo root
if(-not (Test-Path (Join-Path $root "state\STATE.json"))){ Abort "MISSING_STATE" }
if(-not (Test-Path (Join-Path $root "tools"))){ Abort "MISSING_TOOLS" }

# Prepare dirs
$ts = (Get-Date).ToUniversalTime().ToString("yyyyMMddTHHmmssZ")
$artEvidence = Join-Path $root ("artifacts\evidence\{0}_evidence" -f $ts)
$artResume   = Join-Path $root ("artifacts\resume\{0}_resume" -f $ts)
$artDesign   = Join-Path $root ("artifacts\design_review\{0}_design_review" -f $ts)
$appDir      = Join-Path $root "app"
New-Item -ItemType Directory -Force -Path $artEvidence,$artResume,$artDesign,$appDir | Out-Null

# Write app/index.html (UTF-8 no BOM) from Base64
$utf8 = [System.Text.Encoding]::UTF8
$b64  = ""
$bytes = [Convert]::FromBase64String($b64)
[System.IO.File]::WriteAllBytes((Join-Path $appDir "index.html"), $bytes)

# Manifest (hashes)
function Sha256File($p){
  if(-not (Test-Path $p)){ return $null }
  return (Get-FileHash -Algorithm SHA256 -LiteralPath $p).Hash.ToLower()
}

$manifest = [ordered]@{
  ai_signature = "PYM JBZ"
  gate = "G10_DESIGN_REVIEW_PACK"
  ts_utc = $ts
  files = [ordered]@{
    "app/index.html" = (Sha256File (Join-Path $appDir "index.html"))
    "state/STATE.json" = (Sha256File (Join-Path $root "state\STATE.json"))
    "state/LEDGER_v2.ndjson" = (Sha256File (Join-Path $root "state\LEDGER_v2.ndjson"))
    "state/NEXT_ACTION.md" = (Sha256File (Join-Path $root "state\NEXT_ACTION.md"))
  }
}
$manifestPath = Join-Path $artEvidence "manifest.json"
($manifest | ConvertTo-Json -Depth 8) | Out-File -FilePath $manifestPath -Encoding ascii

# QG
$qg = [ordered]@{
  ai_signature = "PYM JBZ"
  gate = "G10_DESIGN_REVIEW_PACK"
  status = "PASS"
  ts_utc = $ts
  outputs = [ordered]@{
    "app/index.html" = "created"
    "design_review_pack" = "created"
  }
}
$qgPath = Join-Path $artEvidence "QG.json"
($qg | ConvertTo-Json -Depth 8) | Out-File -FilePath $qgPath -Encoding ascii

# Zip packs
$evidenceZip = Join-Path $root ("artifacts\evidence\{0}_evidence.zip" -f $ts)
$designZip   = Join-Path $root ("artifacts\design_review\{0}_design_review_pack.zip" -f $ts)
$resumeZip   = Join-Path $root ("artifacts\resume\{0}_resume_pack.zip" -f $ts)

if(Test-Path $evidenceZip){ Remove-Item $evidenceZip -Force }
if(Test-Path $designZip){ Remove-Item $designZip -Force }
if(Test-Path $resumeZip){ Remove-Item $resumeZip -Force }

Compress-Archive -Path (Join-Path $artEvidence "*") -DestinationPath $evidenceZip -Force
Compress-Archive -Path (Join-Path $appDir "index.html") -DestinationPath $designZip -Force

# Resume pack: state + tools + app (minimal, chat-proof)
Compress-Archive -Path (Join-Path $root "state"), (Join-Path $root "tools"), (Join-Path $root "app") -DestinationPath $resumeZip -Force

# Update STATE.json (best-effort; preserve unknown fields)
$statePath = Join-Path $root "state\STATE.json"
$stateRaw = Get-Content -LiteralPath $statePath -Raw
try { $stateObj = $stateRaw | ConvertFrom-Json } catch { Abort "STATE_JSON_PARSE" }

if(-not $stateObj.gate){ $stateObj | Add-Member -NotePropertyName gate -NotePropertyValue (@{}) }
if(-not $stateObj.gate.current){ $stateObj.gate | Add-Member -NotePropertyName current -NotePropertyValue "" -Force }
if(-not $stateObj.gate.last_pass){ $stateObj.gate | Add-Member -NotePropertyName last_pass -NotePropertyValue "" -Force }

$stateObj.gate.current = "G10_DESIGN_REVIEW_PACK"
$stateObj.gate.last_pass = "G10_DESIGN_REVIEW_PACK"

if(-not $stateObj.locked_pass){ $stateObj | Add-Member -NotePropertyName locked_pass -NotePropertyValue @() -Force }
if(($stateObj.locked_pass -notcontains "G10_DESIGN_REVIEW_PACK")){
  $stateObj.locked_pass = @($stateObj.locked_pass + "G10_DESIGN_REVIEW_PACK")
}

# Keep encoding no BOM
[System.IO.File]::WriteAllBytes($statePath, $utf8.GetBytes(($stateObj | ConvertTo-Json -Depth 20)))

# Append ledger_v2 (append-only)
$ledgerPath = Join-Path $root "state\LEDGER_v2.ndjson"
if(-not (Test-Path $ledgerPath)){ New-Item -ItemType File -Force -Path $ledgerPath | Out-Null }
$rec = [ordered]@{
  ts_utc = $ts
  event  = "G10_RUN"
  gate   = "G10_DESIGN_REVIEW_PACK"
  qg_sha256 = (Get-FileHash -Algorithm SHA256 -LiteralPath $qgPath).Hash.ToLower()
  manifest_sha256 = (Get-FileHash -Algorithm SHA256 -LiteralPath $manifestPath).Hash.ToLower()
  evidence_zip_sha256 = (Get-FileHash -Algorithm SHA256 -LiteralPath $evidenceZip).Hash.ToLower()
  resume_zip_sha256 = (Get-FileHash -Algorithm SHA256 -LiteralPath $resumeZip).Hash.ToLower()
  design_review_zip_sha256 = (Get-FileHash -Algorithm SHA256 -LiteralPath $designZip).Hash.ToLower()
}
Add-Content -LiteralPath $ledgerPath -Value ($rec | ConvertTo-Json -Compress) -Encoding ascii

# Output
$qgHash = (Get-FileHash -Algorithm SHA256 -LiteralPath $qgPath).Hash.ToLower()
$manHash = (Get-FileHash -Algorithm SHA256 -LiteralPath $manifestPath).Hash.ToLower()
$evHash = (Get-FileHash -Algorithm SHA256 -LiteralPath $evidenceZip).Hash.ToLower()
$rsHash = (Get-FileHash -Algorithm SHA256 -LiteralPath $resumeZip).Hash.ToLower()
$drHash = (Get-FileHash -Algorithm SHA256 -LiteralPath $designZip).Hash.ToLower()

Write-Host ("G10_DONE qg={0} manifest={1} evidence_zip={2} design_zip={3} resume_zip={4}" -f $qgHash,$manHash,$evHash,$drHash,$rsHash)
exit 0
